import { useEffect, useState } from "react"
import "./App.css";
import { Link, useNavigate } from "react-router-dom";


function Fetch(){

    const[data,setData]=useState([])
    const navigate=useNavigate()
    useEffect(()=>{
       fetch(" http://localhost:3006/posts")
       .then((res)=>{
          return res.json()
       })
       .then((resp)=>{
          setData(resp)
       })
    },[])

    const Dmp = (id) =>{
       navigate("/details/" + id)
    }

    return(
      <div>
            <nav class="navbar navbar-expand-lg shadow p-3 mb-5 bg-body-color-rgb">
                    <div class="container-fluid">
                        <img class="navbar-brand" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS0UwtULR0EQ6e6V0BFypevI70LSWm-jPMpBw&usqp=CAU" style={{width:"100px",height:"80px",marginLeft:"30px"}}/>
                          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                            <span class="navbar-toggler-icon"></span>
                          </button>
           <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
               <div class="navbar-nav" style={{marginLeft:"50px",gap:"80px"}} >
                  <Link to="/" style={{textDecoration:"none"}}>
                    <a class="nav-link active" aria-current="page" href="#">Home</a>
                    </Link>
                    <Link to="/shop"  style={{textDecoration:"none"}}>
                    <a class="nav-link" href="#">Shop</a>
                    </Link>
                    <Link to="/fetch"  style={{textDecoration:"none"}}>
                    <a class="nav-link" href="#">Page</a>
                    </Link>
                    <a class="nav-link" href="#">Collection</a>
                    <a class="nav-link" href="#" style={{fontSize:"25px"}}><i class="fa-solid fa-bag-shopping"></i></a>
                </div>
            </div>
                      </div>
                </nav>
         <div id="pro">
             {data.map((one)=>(
                <div id="a">
                    <img src={one.img} style={{width:"150px",height:"150px",marginLeft:"40px"}} />
                    <h6 style={{opacity:"0.5",marginLeft:"40px"}}>{one.name}</h6>
                    <p style={{marginLeft:"40px"}}>{one.dep}</p>
                    <h6><span style={{color:"mediumseagreen",marginLeft:"40px"}}>$</span>{one.price}</h6>
                    <a className="btn btn-success" style={{marginLeft:"60px"}} onClick={()=>{Dmp(one.id)}} >Details</a>
                 <br/> <br/>
                </div>
             ))}
         </div>
      </div>
    )
}
export default Fetch;
