import { useEffect, useState } from "react"
import "./App.css";
import { Link } from "react-router-dom";


function Fetch(){

    const[data,setData]=useState([])

    useEffect(()=>{
       fetch("http://localhost:3002/posts")
       .then((res)=>{
          return res.json()
       })
       .then((resp)=>{
          setData(resp)
       })
    },[])

